const changeResult=()=>{let t,n=$("#vid-width").val(),e=$("#vid-height").val(),l=$("#vid-fps").val(),a=n*e,i=3686400,p=a/i,d=l/60,s=Math.round(1490*(.95*p+.05*d))/100;t=n&&e&&l&&!isNaN(s)?s+" Mbps":"",$("#result").html(t)};document.addEventListener("DOMContentLoaded",()=>{for(let t of document.querySelectorAll("input-field")){let n=$(t);$(t).html(`<div class="input-group mb-3">\n        <div class="input-group-prepend">\n          <span class="input-group-text">${n.attr("title1")}</span>\n        </div>\n        <input oninput="changeResult()" type="number" class="form-control" aria-label="Amount (to the nearest dollar)" placeholder="${n.attr("placeholder")}" id="${n.attr("_id")}" min="1">\n        <div class="input-group-append">\n          <span class="input-group-text">${n.attr("title2")}</span>\n        </div>\n      </div>`)}});